import { Component } from '@angular/core';

@Component({
  selector: 'app-diretiva-ngif',
  templateUrl: './diretiva-ngif.component.html',
  styleUrls: ['./diretiva-ngif.component.css']
})
export class DiretivaNgifComponent {
  cursos: Array<string> = [];
  mostrarCursos: boolean = false;

  onMostrarCursos() {
    this.mostrarCursos = !this.mostrarCursos;
  }
}
<h3>*ngIf</h3>

<div *ngIf="cursos.length > 0">Lista de cursos.</div>
<div *ngIf="cursos.length == 0">Não há cursos para listar.</div>

<div *ngIf="mostrarCursos">Lista de cursos.</div>
<div *ngIf="!mostrarCursos">Não há cursos para listar.</div>

<div [hidden]="mostrarCursos">Lista de cursos.</div>
<div [hidden]="!mostrarCursos">Não há cursos para listar.</div>

<button (click)="onMostrarCursos()">Mostrar ou esconder cursos</button>

<ng-template [ngIf]="mostrarCursos">
  <div>Lista de cursos.</div>
</ng-template>
