import { Directive, ElementRef } from '@angular/core';

@Directive({
  selector: '[fundoAzul]',
})
export class FundoAzulDirective {
  constructor(private elementRef: ElementRef) {
    const ref = this.elementRef.nativeElement as HTMLElement;
    ref.classList.add('bg-primary');
  }
}
