import { Component } from '@angular/core';

@Component({
  selector: 'app-diretiva-ngstyle',
  templateUrl: './diretiva-ngstyle.component.html',
  styleUrls: ['./diretiva-ngstyle.component.css'],
})
export class DiretivaNgstyleComponent {
  ativo: boolean = false;
  tamanhoFonte: number = 14;
  toggleActive() {
    this.ativo = !this.ativo;
  }
}
<div class="container">
  <h3>Style binding</h3>
  <div class="mb-2">
    <button
      (click)="toggleActive()"
      [style.fontWeight]="ativo ? 'bold' : 'normal'"
    >
      Clique aqui
    </button>
    <div class="mt-2">
      <input type="text" [(ngModel)]="tamanhoFonte" />
    </div>
  </div>
  <h3>ngStyle</h3>
  <div class="mb-2">
    <button (click)="toggleActive()">Clique aqui</button>
  </div>
</div>
